package main.java.database; /** * Created by dylan on 5-12-15. */import main.java.database.ConnectionConfig;import java.sql.*;public class DatabaseController {    Connection connection = null;    public void getConnection(){        try {            connection = ConnectionConfig.getConnection();            if (connection != null){                System.out.println("Connection is established");            }        }        catch (Exception e){            e.printStackTrace();        }    }    public void closeConnection(){        if (connection != null){            try {                connection.close();                System.out.println("Connection is closed");            }            catch (SQLException e){                e.printStackTrace();            }        }    }}