package main.java.database; /** * Created by dylan on 5-12-15. */import main.java.database.ConnectionConfig;import javax.xml.transform.Result;import java.sql.*;public class DatabaseController {    Connection connection = null;    public void setConnection(){        if (connection == null){            try {                connection = ConnectionConfig.getConnection();                if (connection != null){                    System.out.println("Connection is established");                }            }            catch (Exception e){                e.printStackTrace();            }        }    }    public void closeConnection(){        if (connection != null){            try {                connection.close();                System.out.println("Connection is closed");            }            catch (SQLException e){                e.printStackTrace();            }        }    }    public ResultSet executeQuery(String query){        connection = ConnectionConfig.getConnection();        try{            Statement st = connection.createStatement();            ResultSet resultSet = st.executeQuery(query);            return resultSet;        } catch (SQLException e){            e.printStackTrace();            return null;        }    }    public static void main (String[] args) throws SQLException {        DatabaseController dbcon = new DatabaseController();        System.out.println("DataBaseController test");        dbcon.setConnection();        ResultSet rs = dbcon.executeQuery("SELECT * FROM words");        while (rs.next()){            String word = rs.getString("word");            System.out.println(word);        }        dbcon.closeConnection();    }}